import React,{Component} from 'react';
import {Link,withRouter} from 'react-router-dom';

class Daftarlogpintu extends Component{
  constructor(props) {
    super(props);
    this.state = {
    nim: '',
    terminalid:'',
    checkedtmdate:'',
    checkedtmtime:'',
    lockstatus:'',
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit(e){
    e.preventDefault();
    const {nim,terminalid,checkedtmdate, checkedtmtime,lockstatus} = this.state;
    var hasil, checkedtminput;
    checkedtminput = checkedtmdate+" "+checkedtmtime+":00.000000"
    hasil = 'term-id='+terminalid+'&nim='+nim+'&checkedtm='+checkedtminput+'&lockstatus='+lockstatus;
    console.log(hasil)

    // let username = 'admin';
    // let password = 'bandung123';
    // let h = new Headers();
    // h.append ('Content-Type', 'application/x-www-form-urlencoded')
    // h.append ('Authorization', 'Basic ' + btoa(username + ':' + password))
    // fetch('https://192.168.2.7/smartlock/api/v1/addlog.json', {
    //     method: 'POST',
    //     body: hasil,
    //     headers: h
    // })
    // window.location.reload();
}

    render(){
        const {nim, terminalid, checkedtmdate, checkedtmtime, lockstatus} = this.state
        console.log(nim)
        console.log(terminalid)
        console.log(checkedtmdate)
        console.log(checkedtmtime)
        console.log(lockstatus)
        return (
            <div>
                <div className="paddingtop30px">
                </div>
                <div className="kotakfilter2"> 
                    <form className="kotakforminputlogpintu" onSubmit={this.handleSubmit}>
                    <div className="kotakinputlogpintunim">
                        <label> NIM </label> <br></br>
                        <input name="nim" onChange={this.handleChange} className="inputformlogpintunim" type="text" placeholder="NIM" ></input>
                    </div>

                    <div className="kotakinputlogpintuterminalid">
                        <label>Terminal ID </label> <br></br>
                        <input name="terminalid" onChange={this.handleChange} className="inputformlogpintuterminalid" type="text" placeholder="Terminal Id" ></input>
                    </div>

                    <div className="kotakinputlogpintucheckedtm">
                        <label> Checked Time </label> <br></br>
                        <input name="checkedtmdate" onChange={this.handleChange} className="inputformlogpintucheckedtm" type="date" ></input>
                        <input name="checkedtmtime" onChange={this.handleChange} className="inputformlogpintucheckedtm" type="time" ></input>
                    </div> 

                    <div className="kotakinputlogpintustatus">
                        <label> Status </label> <br></br>
                        <select name="lockstatus" onChange={this.handleChange} className="inputformlogpintustatus">
                            <option value="0"> Check In </option>
                            <option value="1"> Check Out </option>
                            <option value="2"> Izin </option>
                            <option value="3"> Sakit </option>
                        </select>
                    </div>

                    <div className="kotaksubmitpengguna">
                        <input className="submitformlogpintu" type="submit" value="Add"></input>
                    </div>

                    <div className="kotakcancelpengguna">
                        <Link to="/logpintu"> <span className="cancelformpengguna">Cancel</span></Link>
                    </div>
                    </form> 
                </div>
            </div>
        )
    } 
}
export default withRouter(Daftarlogpintu);
