import React from 'react';

export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            isidata: [],
            coba: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();
        const {isidata} = this.state
        const {username, password} = this.state

        if (!(username && password)) {
            return;
        }

        let h= new Headers()
        h.append ('Authorization', 'Basic ' + btoa(username + ':' + password))
        fetch('https://192.168.2.7/smartlock/api/v1/login/', {
           method: 'GET',
           headers: h
        })
        .then(data => this.setState({isidata: data}))
        console.log(isidata)
    }

    render(){
        const {isidata} = this.state
        const {username, password} = this.state
        var {coba} = this.state
        console.log (isidata)
        console.log(username)
        console.log(password)


        return (
            <div className="login">
                <img src="img/logo.png"></img>
                <h1 > LOGIN </h1>
                <form className="formform" onSubmit={this.handleSubmit}>
                    {isidata.status}
                    {coba}
                    <h2>NIP/NIM</h2>
                    <input className="inputnim" name="username" value={username}  onChange={this.handleChange} type="text" required/>
                    <br></br>

                    <h2>Password</h2>
                    <input className="inputpass" name="password" value={password} onChange={this.handleChange} type="password" required/>

                    <input className="submitform" type="submit" value="Masuk" />
                </form>
            </div>
        )
    } 
}
