import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route,Link,withRouter,Switch} from "react-router-dom";
import Daftarruangan from './daftarruangan';
import Editruangan from './editruangan';
import "@fortawesome/fontawesome-free/css/all.min.css";

class Ruangan extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isidata: [],
      editID:'',
      showw: false,
      hidew: false,
    };
  }

  componentDidMount(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  refresh(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  deleteData(e,f){
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: ID Ruangan = " +e+", Nama Ruangan= "+f);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deleteRoom/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          id_ruangan: e
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  show(){
    this.setState ({showw:true})
  }

  editData(e){
    this.setState({editID:e})
  }

  render(){
    const {isidata,editID} = this.state
    var x = 1;
    return(
      <div>
          <div>
            <Switch>
              <Route path="/ruangan/daftar" render={ () => <Daftarruangan/> } />
              <Route path="/ruangan/edit" render={ () => <Editruangan editID={editID}/> } />
            </Switch>
          </div>
          <div id="menudaftarruangan" className="kotakfilter"> 
            <div className="paddingtop30px">
            </div>
          </div>
          <div className="paddingtop30px">
          </div>
          <div id="listruangan" className="box-footer">
              <div className="kotakdaftarruangan">
                <a onClick={this.show}>
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </a>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div>
                <table>
                  <thead>
                    <tr>
                      <th>No</th>  
                      <th>ID Ruangan</th>
                      <th>Nama Ruangan</th>
                      <th>Keterangan</th>
                    </tr>
                  </thead>
                  <tbody>
                    {isidata.map(isi =>(
                      <tr key={isi.id_ruangan}>
                        <td>{x++}</td>
                        <td>{isi.kode_ruangan}</td>
                        <td>{isi.nama_ruangan}</td>
                        <td>
                          <div className="editdelete"> 
                            <Link to="/ruangan/edit" onClick={() => this.editData(isi.kode_ruangan)}>
                              <i className="fa fa-pencil"></i>
                            </Link>
                            <span> | </span> 
                            <a className="mousepointer" onClick={() => this.deleteData(isi.kode_ruangan,isi.nama_ruangan)}> 
                              <i className="fa fa-trash"></i>
                            </a>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div> 
          </div>
      </div>
    )
  } 
}
export default withRouter(Ruangan);
