import React, {Component} from 'react';
import {Route,withRouter,Link} from "react-router-dom";
import { AnimatedSwitch } from 'react-router-transition';
import Statistik from './content/statistik';
import Logpintu from './content/logpintu';
import Pengguna from './content/pengguna';
import Ruangan from './content/ruangan';
import Client from './content/client';
import Laporan from './content/laporan';
import Home from './content/home';


class Content extends Component {
    render(){
      document.title="Admin"
        return (
          <div className="content-wrapper">
            <AnimatedSwitch
              atEnter={{ opacity: 0 }}
              atLeave={{ opacity: 0 }}
              atActive={{ opacity: 1 }}
              className="switch-route"
            >
              <Route exact path="/" component={Home} />
              <Route path="/login" render={()=>
                <div className="box-footer">
                  You're Logged In
                  <br></br>
                  <br></br>
                  Go to
                  <Link className="kotakgotohome" to="/">
                    <div className="gotohome">
                      <span>Home
                      </span>
                    </div> 
                  </Link>
                </div>} />
              <Route path="/statistik" render={ () => <Statistik token={this.props.token} /> } />
              <Route path="/logpintu" render={ () => <Logpintu token={this.props.token} /> } />
              <Route path="/pengguna" render={ () => <Pengguna token={this.props.token} /> } />
              <Route path="/ruangan" render={ () => <Ruangan token={this.props.token} /> } />
              <Route path="/laporan" render={ () => <Laporan token={this.props.token} /> } />
              <Route path="/client" render={ () => <Client token={this.props.token} /> } />
              <Route render={()=>
                <div className="box-footer">
                  404 NOT FOUND
                  <br></br>
                  <br></br>
                  Go to
                  <Link className="kotakgotohome" to="/">
                    <div className="gotohome">
                      <span>Home
                      </span>
                    </div> 
                  </Link>
                </div>
              } />
            </AnimatedSwitch>
          </div> 
        )
    }
}

export default withRouter(Content);
