import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route,Link,withRouter,Switch} from "react-router-dom";
import Daftarclient from './daftardevice';
import Editclient from './editdevice';
import "@fortawesome/fontawesome-free/css/all.min.css";

class Device extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isidata: [],
      daftar: false,
      edit:false,
      idc:'',
      ipc:'',
      portrc: '',
      portsc: '',
      idu:'',
      ipu:'',
      portsu:'',
      portru:'',
      datasalah: false,
      databenar: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmitDaftar = this.handleSubmitDaftar.bind(this);
    this.handleSubmitEdit = this.handleSubmitEdit.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmitDaftar(e){
    e.preventDefault();
    const {idc,ipc,portrc,portsc} = this.state
    fetch('http://192.168.2.7:8020/doorlog/regdevais/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        id_ruangan: idc,
        ip_reader: ipc,
        port_reader:portrc,
        port_server_used:portsc
      })
    })
    .then(response => {
      if (response.ok){
        this.setState({databenar:true})
        this.setState({datasalah:false})
      }
      else {
        this.setState({datasalah:true})
        this.setState({databenar:false})
      }
    })
  }

  handleSubmitEdit(e){
    e.preventDefault();
    const {idu,portsu,ipu,portru} = this.state
    fetch('http://192.168.2.7:8020/doorlog/editdevais/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        port_server_used:portsu,
        new_id_ruangan: idu,
        new_ip_reader: ipu,
        new_port_reader: portru,
      })
    })
    .then(response => {
      if (response.ok){
        this.setState({databenar:true})
        this.setState({datasalah:false})
      }
      else {
        this.setState({datasalah:true})
        this.setState({databenar:false})
      }
    })
  }

  componentWillMount(){
    fetch('http://192.168.2.7:8020/doorlog/listdevais/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  componentDidUpdate(){
    fetch('http://192.168.2.7:8020/doorlog/listdevais/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  deleteData(e){
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: Port Server = " +e);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deldevais/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          port_server_used: e
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  showDaftar(){
    this.setState({daftar:true})
  }
  hideDaftar(){
    this.setState({daftar:false})
  }

  showEdit(a){
    this.setState({edit:true})
    this.setState({idu:a})
  }
  hideEdit(){
    this.setState({edit:false})
