import React from 'react';
import logo from './img/logoxirkawarna.png'

export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            isidata: [],
            coba: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();
        const {isidata} = this.state
        const {username, password} = this.state

        if (!(username && password)) {
            return;
        }

        let h= new Headers()
        h.append ('Authorization', 'Basic ' + btoa(username + ':' + password))
        fetch('https://192.168.2.7/smartlock/api/v1/login/', {
           method: 'GET',
           headers: h
        })
        .then(data => this.setState({isidata: data}))
        console.log(isidata)

        sessionStorage.setItem("name",btoa(username + ':' + password))
        window.location.reload();
    }

    render(){       
        const {username,password} = this.state
        console.log(sessionStorage)
        return (
            <div className="login">
                <div className="imglogin">
                    <img src={logo}></img>
                </div>

                <h1 > LOGIN </h1>

                <div className="formform">
                    <form  onSubmit={this.handleSubmit}>
                        <h2 className="loginnim">NIP/NIM</h2>
                        <input className="inputnim" name="username" value={username}  onChange={this.handleChange} type="text" required/>
                        <br></br>

                        <h2 className="loginpass">Password</h2>
                        <input className="inputpass" name="password" value={password} onChange={this.handleChange} type="password" required/>
                        <br></br>
                        <input className="submitform" type="submit" value="Masuk" />
                    </form>
                </div>

            </div>
        )
    } 
}
