import React, { Component } from 'react';
import Header from './components/Header';
import SideBar from './components/SideBar';
import Content from './components/Content';
import Login from './components/content/login';
import {BrowserRouter, Route, Redirect, withRouter } from "react-router-dom";
import './components/content/css/AdminLTE.css';
import './components/content/css/_all-skins.min.css';
import './components/content/css/font-awesome.css';
import './components/content/css/font-awesome.min.css';
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import logo from './components/content/img/logo.png'
// import './components/content/js/581d5d54d2.js';
// import './components/content/js/adminlte.min.js';
// import './components/content/js/bootstrap.min.js';
// import './components/content/js/dashboard2.js';
// import './components/content/js/demo.js';
// import './components/content/js/jquery.min.js';
// import './components/content/js/jquery.slimscroll.min.js';
// import './components/content/js/jquery.sparkline.min.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      isidata: [],
      isLogin:false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount(){
    const {isidata} = this.state
    fetch('https://192.168.2.7/smartlock/api/v1/login/', {
        method: 'GET',
      })
      .then(data => this.setState({isidata: data}))
      console.log(isidata)
  }


  handleChange(e) {
      const { name, value } = e.target;
      this.setState({ [name]: value });
  }

  handleSubmit(e) {
      e.preventDefault();
      const {isidata} = this.state
      const {username, password} = this.state

      let h= new Headers()
      h.append ('Authorization', 'Basic ' + btoa(username + ':' + password))
      fetch('https://192.168.2.7/smartlock/api/v1/login/', {
        method: 'GET',
        headers: h
      })
      .then(data => this.setState({isidata: data}))
      console.log(isidata)
      let a = "coeg"
      localStorage.setItem("name","didi")
  }

    render(){
      const {isidata} = this.state
      const {username, password} = this.state
      var {coba} = this.state
      console.log (isidata)
      console.log(username)
      console.log(password)

      if (localStorage==="didi"){
        return(
          <div>
            <Route path="/" component={Header} />,
            <Route path="/" component={SideBar} />,
            <Route path="/" component={Content} />,
         </div>
        )
      }

      else {
        return (
          <div className="login">
            <Redirect to="/login"/>
              <img src={logo}></img>
              <h1 > LOGIN </h1>
              <form className="formform" onSubmit={this.handleSubmit}>
                  {isidata.status}
                  {coba}
                  <h2 className="loginnim">NIP/NIM</h2>
                  <input className="inputnim" name="username" value={username}  onChange={this.handleChange} type="text" required/>
                  <br></br>

                  <h2 className="loginpass">Password</h2>
                  <input className="inputpass" name="password" value={password} onChange={this.handleChange} type="password" required/>

                  <input className="submitform" type="submit" value="Masuk" />
              </form>
          </div>
        )
      }
  }
}

export default withRouter(App);
