import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route,Link,withRouter,Switch} from "react-router-dom";

import "@fortawesome/fontawesome-free/css/all.min.css";
import Daftarruanganuser from './daftarruanganuser';

class Ruanganuser extends Component{
  constructor(props) {
    super(props);
    this.state = {
        idl:'',
        isidata: [],
        dataruangan: [],
        daftar: false,
        nimc:'',
        idc:'',
        datasalah: false,
        databenar: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmitDaftar = this.handleSubmitDaftar.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmitDaftar(e){
    e.preventDefault();
    const {nimc,idc} = this.state
    fetch('http://192.168.2.7:8020/doorlog/createPermission/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        nim: nimc,
        id_ruangan: idc
      })
    })
    .then(response => {
      if (response.ok){
        this.setState({databenar:true})
        this.setState({datasalah:false})
      }
      else {
        this.setState({datasalah:true})
        this.setState({databenar:false})
      }
    })
  }

  componentWillMount(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
    })
    .then (response =>response.json())  
    .then (response =>this.setState({dataruangan:response.list}))
  }

  componentDidUpdate(){
    const {idl} = this.state
    if ((idl!=='')&&(idl!=="Pilih ID Ruangan")){
        fetch('http://192.168.2.7:8020/doorlog/users/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        id_ruangan: idl,
      })
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
    }
  }

  deleteData(e,f){
    const {idl} = this.state
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: NIM = " +e+", ID Ruangan= "+f);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deletePermission/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          nim: e,
          id_ruangan: idl
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  showDaftar(){
    this.setState({daftar:true})
  }
  hideDaftar(){
    this.setState({daftar:false})
  }

  render(){
    const {daftar,databenar,datasalah,idl,dataruangan} = this.state
    var x = 1;
    function no(i){
      var m=0
      var hasil=m+i
      return  hasil
    }
    const data = {
      columns: [
        {
          label: 'No',
          field: 'no',
          sort: 'asc',
        },
        {
          label: 'NIM',
          field: 'nim',
          sort: 'asc',
        },
        {
          label: 'Nama Pengguna',
          field: 'namapengguna',
          sort: 'asc',
        },
        {
          label: 'Hapus',
          field: 'hapus'
        }
      ],
      rows: this.state.isidata.map(isi=>{
        return {
          no:no(x++),
          nim: isi.nim,
          namapengguna:isi.pengguna,
          hapus:<div className="editdelete"> <a className="mousepointer" onClick={() => this.deleteData(isi.nim,isi.pengguna)}> <i className="fa fa-trash"></i></a> </div>
        }
      })
    };
    var aksidata
    if (daftar===true){
      aksidata = "show"
    }
    else {
      aksidata = "hide"
    }
    return(
      <div>
          <div>
            <Switch>
              <Route path="/ruanganuser/daftar" render={ () => <Daftarruanganuser/> } />
            </Switch>
          </div>
          <div className="box-footer">
              <div>
                  <form onSubmit={this.handleSubmitList}>
                        <label>ID Ruangan</label>
                        <select name="idl" onChange={this.handleChange}>
                        <option>Pilih ID Ruangan</option>
                            {dataruangan.map(isi=>(
                                <option key={isi.kode_ruangan} value={isi.kode_ruangan}>{isi.kode_ruangan}</option>
                            ))}
                        </select>
                        <button type="submit">Cari</button>
                  </form>
              </div>
              <div className="kotakdaftarruangan">
                <Link to="/ruanganuser/daftar">
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </Link>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div>
                <table>
                  <thead>
                    <tr>
                      <th>No</th>  
                      <th>Pengguna</th>
                      <th>Hapus</th>
                    </tr>
                  </thead>
                  <tbody>
                    {isidata.map(isi =>(
                      <tr key={isi.pengguna}>
                        <td>{x++}</td>
                        <td>{isi.pengguna}</td>
                        <td>
                          <div className="editdelete"> 
                            <a className="mousepointer" onClick={() => this.deleteData(isi.pengguna,idl)}> 
                              <i className="fa fa-trash"></i>
                            </a>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div> 
          </div>
      </div>
    )
  }
}
export default withRouter(Ruanganuser);
