import React from 'react';
import logo from './img/logo.png'
import dataclient from "./../../user";

export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            coba: '',
            gagal:false,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();
        const {user,username, password} = this.state
        let h= new Headers()
        h.append ('Authorization', 'Basic ' + btoa(username + ':' + password))
        fetch('https://192.168.2.7/smartlock/api/v1/login/', {
           method: 'GET',
           headers: h
        })
        .then(response => {
            if(response.status === 200) {
                sessionStorage.setItem("message","admin")
                window.location.reload()
            }
            else{
                for (var i=0;i<dataclient.length;i++){
                    if ((username===dataclient[i].username)&&(password===dataclient[i].password)){
                        sessionStorage.setItem("message",dataclient[i].name)
                        window.location.reload()
                        break
                    }
                    else {
                        this.setState({gagal:true})
                    }
                }
            }
        })        
    }

    render(){
        document.title="Login"
        const {gagal,username,password} = this.state;
        return (
            <div className="login">
                <div className="imglogin">
                    <img src={logo}></img>
                </div>

                <h1 > LOGIN </h1>

                <div className="formform">
                    <form  onSubmit={this.handleSubmit}>
                        <h2 className="loginnim">Username</h2>
                        <input className="inputnim" name="username" value={username}  onChange={this.handleChange} type="text" required/>
                        <br></br>

                        <h2 className="loginpass">Password</h2>
                        <input className="inputpass" name="password" value={password} onChange={this.handleChange} type="password" required/>
                        <br></br>
                        {
                            gagal &&
                            <p className="gagallogin">*username or password is incorrect</p>
                        }
                        <button className="submitform" type="submit"> 
                            <span>
                                <span>
                                    Masuk&nbsp;
                                </span>
                                <i className="fa fa-sign-in"> 
                                </i>
                            </span>
                        </button>
                    </form>
                </div>
            </div>
        )
    } 
}
