import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route,Link,withRouter,Switch} from "react-router-dom";
import Daftarclient from './daftardevice';
import Editclient from './editdevice';
import "@fortawesome/fontawesome-free/css/all.min.css";

class Ruanganuser extends Component{
  constructor(props) {
    super(props);
    this.state = {
        idl:'',
        isidata: [],
        editID:'',
    };
  }

  componentDidMount(){
    const {idl} = this.state
    fetch('http://192.168.2.7:8020/doorlog/users/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        id_ruangan: idl,
      })
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  refresh(){
    const {idl} = this.state
    fetch('http://192.168.2.7:8020/doorlog/users/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        id_ruangan: idl,
      })
    })
    .then (response =>response.json())  
    .then (response =>console.log(response))
  }

  deleteData(e,f){
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: ID Ruangan = " +e+", Nama Ruangan= "+f);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deleteRoom/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          id_ruangan: e
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  editData(e){
    this.setState({editID:e})
  }

  render(){
    const {isidata,editID} = this.state
    var x = 1;
    return(
      <div>
          <div>
            <Switch>
              <Route path="/device/daftar" render={ () => <Daftarclient/> } />
              <Route path="/device/edit" render={ () => <Editclient editID={editID}/> } />
            </Switch>
          </div>
          <div className="box-footer">
              <div className="kotakdaftarruangan">
                <Link to="/device/daftar">
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </Link>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div>
                <table>
                  <thead>
                    <tr>
                      <th>No</th>  
                      <th>ID Ruangan</th>
                      <th>Nama Ruangan</th>
                      <th>Keterangan</th>
                    </tr>
                  </thead>
                  <tbody>
                    {isidata.map(isi =>(
                      <tr key={isi.id_ruangan}>
                        <td>{x++}</td>
                        <td>{isi.id_ruangan}</td>
                        <td>{isi.nama_ruangan}</td>
                        <td>
                          <div className="editdelete"> 
                            <Link to="/device/edit" onClick={() => this.editData(isi.id_ruangan)}>
                              <i className="fa fa-pencil"></i>
                            </Link>
                            <span> | </span> 
                            <a className="mousepointer" onClick={() => this.deleteData(isi.id_ruangan,isi.nama_ruangan)}> 
                              <i className="fa fa-trash"></i>
                            </a>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div> 
          </div>
      </div>
    )
  }
}
export default withRouter(Ruanganuser);
