import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {withRouter} from 'react-router-dom';

class Logpintu extends Component{
  constructor(props) {
    super(props);
    this.state = {
      sort:'nim',
      ascdsc:'asc',
      pagesize: 10,
      pagee: 1,
      startDate: new Date('2019-07-13'),
      endDate: new Date(),
      searching:'',
      limit:0,
      isidata: [],
      daftar:false,
      edit:false,
      nimc:'',
      ruanganc:'',
      methodc:1,
      nimu: '',
      ruanganu:'',
      dateu:'',
      statusu:1,
      datasalah: false,
      databenar: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmitEdit = this.handleSubmitEdit.bind(this);
    this.handleSubmitDaftar = this.handleSubmitDaftar.bind(this);    
  }
  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }
  componentDidMount(){
    const {sort,ascdsc,pagesize,pagee,startDate,endDate,searching}=this.state
    var filterstart,filterend,startDatee,endDatee,starttahun,startbulan,sb,starttanggal,st,endtahun,endbulan,eb,endtanggal,et
    startDatee=new Date(startDate)
    starttahun=startDatee.getFullYear()
    sb=startDatee.getMonth()+1
    if (sb<=9){
      startbulan="0"+String(sb)
    }
    else {
      startbulan=String(sb)
    }
    st=startDatee.getDate()
    if (st<=9){
      starttanggal="0"+String(st)
    }
    else {
      starttanggal=String(st)
    }
    filterstart=starttahun+"-"+startbulan+"-"+starttanggal;

    endDatee= new Date(endDate)
    endtahun=endDatee.getFullYear()
    eb=endDatee.getMonth()+1
    if (eb<=9){
      endbulan="0"+String(eb)
    }
    else {
      endbulan=String(eb)
    }
    et=endDatee.getDate()
    if (et<=9){
      endtanggal="0"+String(et)
    }
    else {
      endtanggal=String(et)
    }
    filterend=endtahun+"-"+endbulan+"-"+endtanggal;

    fetch('http://192.168.2.7:8020/doorlog/getlogapi/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        sort_by: sort,
        ascordsc: ascdsc,
        page_size: pagesize,
        page: pagee,
        date_start: filterstart,
        date_end: filterend,
        search: searching
      })
    })
    .then (response =>response.json())  
    .then (response =>{
      this.setState({limit:response.length})
      this.setState({isidata:response.list})})
    var x=pagee*pagesize-(pagesize-1);
  }

  refresh(){
    const {sort,ascdsc,pagesize,pagee,startDate,endDate,searching}=this.state
    var filterstart,filterend,startDatee,endDatee,starttahun,startbulan,sb,starttanggal,st,endtahun,endbulan,eb,endtanggal,et
    startDatee=new Date(startDate)
    starttahun=startDatee.getFullYear()
    sb=startDatee.getMonth()+1
    if (sb<=9){
      startbulan="0"+String(sb)
    }
    else {
      startbulan=String(sb)
    }
    st=startDatee.getDate()
    if (st<=9){
      starttanggal="0"+String(st)
    }
    else {
      starttanggal=String(st)
    }
    filterstart=starttahun+"-"+startbulan+"-"+starttanggal;

    endDatee= new Date(endDate)
    endtahun=endDatee.getFullYear()
    eb=endDatee.getMonth()+1
    if (eb<=9){
      endbulan="0"+String(eb)
    }
    else {
      endbulan=String(eb)
    }
    et=endDatee.getDate()
    if (et<=9){
      endtanggal="0"+String(et)
    }
    else {
      endtanggal=String(et)
    }
    filterend=endtahun+"-"+endbulan+"-"+endtanggal;
    fetch('http://192.168.2.7:8020/doorlog/getlogapi/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        sort_by: sort,
        ascordsc: ascdsc,
        page_size: pagesize,
        page: pagee,
        date_start: filterstart,
        date_end: filterend,
        search: searching
      })
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  handleSubmitDaftar(e){
    e.preventDefault();
    const {nimc,ruanganc,methodc} = this.state
    fetch('http://192.168.2.7:8020/doorlog/addlog/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        nim: nimc,
        ruangan: ruanganc,
        method:methodc
      })
    })
    .then(response => {
      if (response.ok){
        this.setState({databenar:true})
        this.setState({datasalah:false})
      }
      else {
        this.setState({datasalah:true})
        this.setState({databenar:false})
      }
    })
  }

  handleSubmitEdit(e){
    e.preventDefault();
    const {nimu,ruanganu,dateu,statusu} = this.state;
    var dateinput, timeinput, t,tahun, b, bulan, ta, tanggal, j, jam, m, menit;
    t = new Date(dateu)
    tahun = String(t.getFullYear());
    b = t.getMonth() + 1;
    if (b<=9){
        bulan = "0"+String(b)
    }
    else {
        bulan = String(b)
    }
    ta = t.getDate();
    if (ta<=9){
        tanggal = "0"+String(ta)
    }
    else {
        tanggal = String(ta)
    }
    j = t.getHours()
    if (j<=9){
        jam = "0"+String(j)
    }
    else {
        jam = String(j)
    }
    m = t.getMinutes()
    if (m<=9){
        menit = "0"+String(m)
    }
    else {
        menit = String(m)
    }

    dateinput = tahun+"-"+bulan+"-"+tanggal
    timeinput = jam+":"+menit+":00.000000"

    fetch('http://192.168.2.7:8020/doorlog/editlog/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        user_id: sessionStorage.user,
        nim: nimu,
        ruangan: ruanganu,
        date: dateinput,
        time: timeinput,
        status: statusu
      })
    })
    .then(response => {
      if (response.ok){
        this.setState({databenar:true})
        this.setState({datasalah:false})
      }
      else {
        this.setState({datasalah:true})
        this.setState({databenar:false})
      }
    })
  }

