import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route,Link,withRouter,Switch} from "react-router-dom";
import Daftarruangan from './daftarruangan';
import Editruangan from './editruangan';
import "@fortawesome/fontawesome-free/css/all.min.css";

class Ruangan extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isidata: [],
      editID:'',
      daftar: false,
      edit:false,
    };
  }

  componentWillMount(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  componentDidUpdate(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  deleteData(e,f){
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: ID Ruangan = " +e+", Nama Ruangan= "+f);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deleteRoom/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          id_ruangan: e
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  showDaftar(){
    this.setState({daftar:true})
  }
  hideDaftar(){
    this.setState({daftar:false})
  }

  showEdit(){
    this.setState({edit:true})
  }
  hideEdit(){
    this.setState({edit:false})
  }

  editData(e){
    this.setState({editID:e})
  }

  render(){
    const {editID,daftar,edit} = this.state
    var x = 1;
    function no(i){
      var m=0
      var hasil=m+i
      return  hasil
    }
    const data = {
      columns: [
        {
          label: 'No',
          field: 'no',
          sort: 'asc',
        },
        {
          label: 'ID Ruangan',
          field: 'idruangan',
          sort: 'asc',
        },
        {
          label: 'Nama Ruangan',
          field: 'namaruangan',
          sort: 'asc',
        },
        {
          label: 'Keterangan',
          field: 'keterangan'
        }
      ],
      rows: this.state.isidata.map(isi=>{
        return {
          no:no(x++),
          idruangan: isi.kode_ruangan,
          namaruangan:isi.nama_ruangan,
          keterangan:<div className="editdelete"> <Link to="/pengguna/edit" onClick={() => this.editData(isi.card_id)}><i className="fa fa-pencil"></i></Link> | <a className="mousepointer" onClick={() => this.deleteData(isi.card_id,isi.card_id,isi.name,isi.instansi)}> <i className="fa fa-trash"></i></a> </div>
        }
      })
    };

    var aksidata,aksidaftar,aksiedit
    if (daftar===true){
      aksidata = "show"
      aksidaftar = "show"
      aksiedit = "hide"
    }
    else if (edit===true){
      aksidata = "show"
      aksidaftar = "hide"
      aksiedit = "hide"
    }
    else {
      aksidata = "hide"
      aksidaftar = "hide"
      aksiedit = "hide"
    }
    return(
      <div>
          <div id={aksidata} className="box-footer">
              <div className="kotakdaftarruangan">
                <a onClick={() => this.showDaftar()}>
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </a>
              </div>
              <div className="isitabel">
                <MDBDataTable
                responsive
                displayEntries={false}
                info={false}
                paging={false}
                striped
                bordered
                small
                hover
                data={data}
                />
              </div>  
          </div>
      </div>
    )
  } 
}
export default withRouter(Ruangan);
