import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route, Link,withRouter,Switch} from "react-router-dom";
import Daftarpengguna from './daftarpengguna';
import Editpengguna from './editpengguna';
import "@fortawesome/fontawesome-free/css/all.min.css";


class Pengguna extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isidata: [],
      editID:'',
    };
  }

  componentDidMount(){
    fetch('http://192.168.2.7:8020/doorlog/allusers/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  refresh(){
    fetch('http://192.168.2.7:8020/doorlog/allusers/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  deleteData(e,f){
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: NIM = " +e+", Nama= "+f);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deleteUser/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          nim: e
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  editData(e){
    this.setState({editID:e})
  }

  render(){
    const {isidata,editID} = this.state
    var x = 1;
    return(
      <div>
          <div>
            <Switch>
              <Route path="/pengguna/daftar" render={ () => <Daftarpengguna/> } />
              <Route path="/pengguna/edit" render={ () => <Editpengguna editID={editID}/> } />
            </Switch>
          </div>
          <div className="box-footer">
              <div className="kotakdaftarruangan">
                <Link to="/pengguna/daftar">
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Pengguna </span>
                  </div>
                </Link>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div>
                <table>
                  <thead>
                    <tr>
                      <th>No</th>  
                      <th>NIM</th>
                      <th>Nama</th>
                      <th>Keterangan</th>
                    </tr>
                  </thead>
                  <tbody>
                    {isidata.map(isi =>(
                      <tr key={isi.nim}>
                        <td>{x++}</td>
                        <td>{isi.nim}</td>
                        <td>{isi.name}</td>
                        <td>
                          <div className="editdelete"> 
                            <Link to="/pengguna/edit" onClick={() => this.editData(isi.nim)}>
                              <i className="fa fa-pencil"></i>
                            </Link>
                            <span> | </span> 
                            <a className="mousepointer" onClick={() => this.deleteData(isi.nim,isi.name)}> 
                              <i className="fa fa-trash"></i>
                            </a>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div> 
          </div>
      </div>
    )
  }  
}

export default withRouter(Pengguna);
