import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {Route,Link,withRouter,Switch} from "react-router-dom";
import Daftarruangan from './daftarruangan';
import Editruangan from './editruangan';
import "@fortawesome/fontawesome-free/css/all.min.css";

class Ruangan extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isidata: [],
      editID:'',
      action: false,
    };
  }

  componentDidMount(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  refresh(){
    fetch('http://192.168.2.7:8020/doorlog/rooms/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  deleteData(e,f){
    var yes = window.confirm("Apakah anda yakin ingin menghapus data berikut: ID Ruangan = " +e+", Nama Ruangan= "+f);
    if (yes === true){
      fetch('http://192.168.2.7:8020/doorlog/deleteRoom/', {
        method: 'post',
        headers :{
          "Authorization" : "Bearer "+ sessionStorage.name,
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          id_ruangan: e
          })
      })
      .then(response=>{
        if (response.ok){
          window.alert("Data berhasil dihapus")
        }
        else{
          window.alert("Data tidak berhasil dihapus")
        }
      })
    }
  }

  show(){
    this.setState({action:true})
  }
  hide(){
    this.setState({action:false})
  }

  editData(e){
    this.setState({editID:e})
  }

  render(){
    const {isidata,editID,action} = this.state
    var aksi
    if (action===true){
      aksi = "show"
    }
    else if (action===false){
      aksi = "hide"
    }
    var x = 1;
    function no(i){
      var m=0
      var hasil=m+i
      return  hasil
    }
    const data = {
      columns: [
        {
          label: 'No',
          field: 'no',
          sort: 'asc',
        },
        {
          label: 'ID Ruangan',
          field: 'idruangan',
          sort: 'asc',
        },
        {
          label: 'Nama Ruangan',
          field: 'namaruangan',
          sort: 'asc',
        },
        {
          label: 'Keterangan',
          field: 'keterangan'
        }
      ],
      rows: this.state.isidata.map(isi=>{
        return {
          no:no(x++),
          idruangan: isi.kode_ruangan,
          namaruangan:isi.nama_ruangan,
          keterangan:<div className="editdelete"> <Link to="/pengguna/edit" onClick={() => this.editData(isi.card_id)}><i className="fa fa-pencil"></i></Link> | <a className="mousepointer" onClick={() => this.deleteData(isi.card_id,isi.card_id,isi.name,isi.instansi)}> <i className="fa fa-trash"></i></a> </div>
        }
      })
    };
    return(
      <div>
          <div>
            <Switch>
              <Route path="/ruangan/daftar" render={ () => <Daftarruangan/> } />
              <Route path="/ruangan/edit" render={ () => <Editruangan editID={editID}/> } />
            </Switch>
          </div>
          <div className="kotakfilter" id={aksi}> 
            <div className="paddingtop30px">
              <a onClick={() => this.hide()}>
                atas
              </a>
            </div>
          </div>

          <div className="paddingtop30px">
          </div>
          <div id={aksi} className="box-footer">
              <div className="kotakdaftarruangan">
                <a onClick={() => this.show()}>
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </a>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div className="isitabel">
                <MDBDataTable
                responsive
                displayEntries={false}
                striped
                bordered
                small
                hover
                data={data}
                />
              </div>  
          </div>
      </div>
    )
  } 
}
export default withRouter(Ruangan);
