import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {withRouter,Route,Link,Switch} from 'react-router-dom';
import Daftarlogpintu from './daftarlogpintu';
import Editlogpintu from './editlogpintu';
import { start } from 'repl';



class Logpintu extends Component{
  constructor(props) {
    super(props);
    this.state = {
      sort:'nim',
      ascdsc:'asc',
      pagesize: 10,
      pagee: 1,
      startDate: new Date('2019-02-13'),
      endDate: new Date(),
      isidata: [],
      daftar:false,
      edit:false,
    };
  }

  componentDidMount(){
    const {sort,ascdsc,pagesize,pagee,startDate,endDate}=this.state
    var filterstart,filterend,starttahun,startbulan,sb,starttanggal,st,endtahun,endbulan,eb,endtanggal,et
    starttahun=startDate.getFullYear()
    sb=startDate.getMonth()+1
    if (sb<=9){
      startbulan="0"+String(sb)
    }
    else {
      startbulan=String(sb)
    }
    st=startDate.getDate()
    if (st<=9){
      starttanggal="0"+String(st)
    }
    else {
      starttanggal=String(st)
    }
    filterstart=starttahun+"-"+startbulan+"-"+starttanggal;
    console.log(filterstart)
    fetch('http://192.168.2.7:8020/doorlog/getlogapi/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        sort_by: sort,
        ascordsc: ascdsc,
        page_size: pagesize,
        page: pagee,
        date_start: startDate,
        date_end: endDate
      })
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  componentDidUpdate(){
    fetch('http://192.168.2.7:8020/doorlog/getlogapi/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        sort_by: "nim",
        ascordsc: "asc",
        page_size: 10,
        page: 1,
        date_start: "2019-02-13",
        date_end:"2019-07-10"
      })
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  render(){
    const {isidata,editID,daftar,edit,startDate,endDate} = this.state
    var x = 1;
    function no(i){
      var m=0
      var hasil=m+i
      return  hasil
    }
    const data = {
      columns: [
        {
          label: 'No',
          field: 'no',
          sort: 'asc',
        },
        {
          label: 'NIM',
          field: 'nim',
          sort: 'asc',
        },
        {
          label: 'Nama',
          field: 'nama',
          sort: 'asc',
        },
        {
          label: 'ID Ruangan',
          field: 'idruangan',
          sort: 'asc',
        },
        {
          label: 'Nama Ruangan',
          field: 'namaruangan',
          sort: 'asc',
        },
        {
          label: 'Tanggal',
          field: 'tanggal',
          sort: 'asc',
        },
        {
          label: 'Waktu',
          field: 'waktu',
          sort: 'asc',
        },
        {
          label: 'Status',
          field: 'status',
          sort: 'asc',
        },
        {
          label: 'Method',
          field: 'method',
          sort: 'asc',
        },
        {
          label: 'Keterangan',
          field: 'keterangan'
        }
      ],
      rows: this.state.isidata.map(isi=>{
        return {
          no:no(x++),
          nim: isi.nim,
          nama: isi.nim,
          koderuangan: isi.nim,
          namaruangan: isi.nim,
          tanggal: isi.nim,
          waktu: isi.nim,
          status: isi.nim,
          method: isi.nim,
          keterangan:<div className="editdelete"> <a onClick={() => this.showEdit(isi.nim)}><i className="fa fa-pencil"></i></a> | <a className="mousepointer" onClick={() => this.deleteData(isi.nim,isi.nama)}> <i className="fa fa-trash"></i></a> </div>
        }
      })
    };
    var aksidata
    if (daftar===true){
      aksidata = "show"
    }
    else if (edit===true){
      aksidata = "show"
    }
    else {
      aksidata = "hide"
    }
    return(
      <div>
          <div>
            <Switch>
              <Route path="/logpintu/daftar" render={ () => <Daftarlogpintu/> } />
              <Route path="/logpintu/edit" render={ () => <Editlogpintu editID={editID}/> } />
            </Switch>
          </div>
          <div className="box-footer">
              <div className="kotakdaftarruangan">
                <Link to="/logpintu/daftar">
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </Link>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div id={aksidata} className="kotakdata">
                <div className="isitabel">
                  <MDBDataTable
                  responsive
                  displayEntries={false}
                  info={false}
                  paging={false}
                  striped
                  bordered
                  small
                  hover
                  theadColor={""}
                  data={data}
                  />
                </div>  
            </div> 
          </div>
      </div>
    )
  } 
}

export default withRouter(Logpintu);
