import React,{Component} from 'react';
import { MDBDataTable } from 'mdbreact';
import {withRouter,Route,Link,Switch} from 'react-router-dom';
import Daftarlogpintu from './daftarlogpintu';
import Editlogpintu from './editlogpintu';



class Logpintu extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isidata: [],
      editID:'',
      daftar:false,
      edit:false,
    };
  }

  componentDidMount(){
    fetch('http://192.168.2.7:8020/doorlog/getlogapi/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        sort_by: "nim",
        ascordsc: "asc",
        page_size: 10,
        page: 1,
        date_start: "2019-02-13",
        date_end:"2019-07-10"
      })
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  refresh(){
    fetch('http://192.168.2.7:8020/doorlog/getlogapi/', {
      method: 'post',
      headers :{
        "Authorization" : "Bearer "+ sessionStorage.name,
        "Content-Type" : "application/json"
      }
    })
    .then (response =>response.json())  
    .then (response =>this.setState({isidata:response.list}))
  }

  editData(e){
    this.setState({editID:e})
  }

  render(){
    const {isidata,editID} = this.state
    var x = 1;
    function no(i){
      var m=0
      var hasil=m+i
      return  hasil
    }
    const data = {
      columns: [
        {
          label: 'No',
          field: 'no',
          sort: 'asc',
        },
        {
          label: 'NIM',
          field: 'nim',
          sort: 'asc',
        },
        {
          label: 'Nama',
          field: 'nama',
          sort: 'asc',
        },
        {
          label: 'ID Ruangan',
          field: 'idruangan',
          sort: 'asc',
        },
        {
          label: 'Nama Ruangan',
          field: 'namaruangan',
          sort: 'asc',
        },
        {
          label: 'Tanggal',
          field: 'tanggal',
          sort: 'asc',
        },
        {
          label: 'Waktu',
          field: 'waktu',
          sort: 'asc',
        },
        {
          label: 'Status',
          field: 'status',
          sort: 'asc',
        },
        {
          label: 'Method',
          field: 'method',
          sort: 'asc',
        },
        {
          label: 'Keterangan',
          field: 'keterangan'
        }
      ],
      rows: this.state.isidata.map(isi=>{
        return {
          no:no(x++),
          nim: isi.nim,
          nama: isi.nim,
          koderuangan: isi.nim,
          namaruangan: isi.nim,
          tanggal: isi.nim,
          waktu: isi.nim,
          status: isi.nim,
          method: isi.nim,
          keterangan:<div className="editdelete"> <a onClick={() => this.showEdit(isi.nim)}><i className="fa fa-pencil"></i></a> | <a className="mousepointer" onClick={() => this.deleteData(isi.nim,isi.nama)}> <i className="fa fa-trash"></i></a> </div>
        }
      })
    };
    var aksidata
    if (daftar===true){
      aksidata = "show"
    }
    else if (edit===true){
      aksidata = "show"
    }
    else {
      aksidata = "hide"
    }
    return(
      <div>
          <div>
            <Switch>
              <Route path="/logpintu/daftar" render={ () => <Daftarlogpintu/> } />
              <Route path="/logpintu/edit" render={ () => <Editlogpintu editID={editID}/> } />
            </Switch>
          </div>
          <div className="box-footer">
              <div className="kotakdaftarruangan">
                <Link to="/logpintu/daftar">
                  <div className="daftar">
                    <i className="fa fa-plus"></i> 
                    <span> Ruangan </span>
                  </div>
                </Link>
                <span>
                  <a onClick={() => this.refresh()}>
                    <div className="daftar2">
                      <i className="fa fa-refresh"></i>
                    </div>
                  </a>
                </span>
              </div>
              <div id={aksidata} className="kotakdata">
                <div className="isitabel">
                  <MDBDataTable
                  responsive
                  displayEntries={false}
                  info={false}
                  paging={false}
                  striped
                  bordered
                  small
                  hover
                  theadColor={""}
                  data={data}
                  />
                </div>  
            </div> 
          </div>
      </div>
    )
  } 
}

export default withRouter(Logpintu);
