import React, {Component} from 'react';
import logo from './content/img/logoxirka.png';
import {Link,withRouter} from 'react-router-dom';

class SideBar extends Component {
    render(){
        function statistik(){
            sessionStorage.setItem("menu",1)
        }
        function logpintu(){
            sessionStorage.setItem("menu",2)
        }
        function pengguna(){
            sessionStorage.setItem("menu",3)
        }
        function ruangan(){
            sessionStorage.setItem("menu",4)
        }
        function ruanganuser(){
            sessionStorage.setItem("menu",5)
        }
        function device(){
            sessionStorage.setItem("menu",6)
        }
        return (
            <div className="main-sidebar">
                <div className="sidebar">
                    <div>
                        <div>
                            <div className="user-panel">
                                <div className="pull-left image">                                        
                                    <img src={logo} className="img-circle" alt="User Image" />
                                </div>
                                <div className="pull-left info">
                                    <p className="tulisanadmin">ADMIN</p>
                                </div>
                            </div>
                        </div>
                        <div className="scrollsidebar">
                            <ul className="sidebar-menu">
                                <li className="header"></li>
                                <li>
                                    {   
                                        (statistik) &&
                                        <Link to="/statistik" className="dipilih" >
                                            <i className="fa fa-bar-chart"></i>
                                            <span>Statistik</span>
                                        </Link>
                                    }
                                    {
                                        (statistik===false) &&
                                        <Link to="/statistik" onClick={() => showStatistik()}>
                                            <i className="fa fa-bar-chart"></i>
                                            <span>Statistik</span>
                                        </Link>
                                    }
                                </li>
                                <li>
                                    {
                                        logpintu &&
                                        <Link to="/logpintu" className="dipilih">
                                            <i className="fa fa-sign-in"></i> 
                                            <span>Log Pintu</span>
                                        </Link>
                                    }
                                    {
                                        (logpintu===false)&&
                                        <Link to="/logpintu" onClick={() => showLogpintu()}>
                                            <i className="fa fa-sign-in"></i> 
                                            <span>Log Pintu</span>
                                        </Link>
                                    }

                                </li>
                                <li>
                                    {
                                        pengguna &&
                                        <Link to="/pengguna" className="dipilih">
                                            <i className="fa fa-users"></i>
                                            <span>Pengguna</span>
                                        </Link>
                                    }
                                    {
                                        (pengguna===false) &&
                                        <Link to="/pengguna" onClick={() => showPengguna()}>
                                            <i className="fa fa-users"></i>
                                            <span>Pengguna</span>
                                        </Link>
                                    }
                                </li>
                                <li>
                                    {
                                        ruangan &&
                                        <Link to="/ruangan" className="dipilih">
                                            <i className="fa fa-home"></i> 
                                            <span>Ruangan</span>
                                        </Link>
                                    }
                                    {
                                        (ruangan===false) &&
                                        <Link to="/ruangan" onClick={() => showRuangan()}>
                                            <i className="fa fa-home"></i> 
                                            <span>Ruangan</span>
                                        </Link>
                                    }
                                </li>
                                <li>
                                    {
                                        ruanganuser &&
                                        <Link to="/ruanganuser" className="dipilih">
                                            <i className="fa fa-user"></i> 
                                            <span>Ruangan Pengguna</span>
                                        </Link>
                                    }
                                    {
                                        (ruanganuser===false) &&
                                        <Link to="/ruanganuser" onClick={() => showRuanganuser()}>
                                            <i className="fa fa-user"></i> 
                                            <span>Ruangan Pengguna</span>
                                        </Link>
                                    }
                                </li>
                                <li>
                                    {
                                        device &&
                                        <Link to="/device" className="dipilih">
                                            <i className="fa fa-credit-card"></i> 
                                            <span>Device</span>
                                        </Link>
                                    }
                                    {
                                        (device===false) &&
                                        <Link to="/device" onClick={() => showDevice()}>
                                            <i className="fa fa-credit-card"></i> 
                                            <span>Device</span>
                                        </Link>
                                    }
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div> 
        )
    }
}

export default withRouter(SideBar);
