import React, {Component} from 'react';
import {Route,Switch,withRouter,Link} from "react-router-dom";
import Statistik from './content/statistik';
import Logpintu from './content/logpintu';
import Pengguna from './content/pengguna';
import Ruangan from './content/ruangan';
import Laporan from './content/laporan';
import Daftarruangan from './content/daftarruangan'
import Home from './content/home';
import Daftarpengguna from './content/daftarpengguna';


class Content extends Component {
    constructor(props) {
        super(props);
        this.state = {
        key: '',
        countdata: '',
        };
      }

    componentDidMount(){
      fetch('http://192.168.2.7:3000/login', {
        method: 'post',
        body: 'username=admin&password=bandung123',
        headers: { 'Content-type': 'application/x-www-form-urlencoded' }
      })
      .then (response=>response.json())
      .then (data => this.setState({key: data.token}))

      let username = 'admin';
      let password = 'bandung123';
      let h = new Headers;
      h.append ('Authorization', 'Basic ' + btoa(username + ':' + password))
      fetch('https://192.168.2.7/smartlock/api/v1/smartlockview.json?limit='+this.props.limit, {
        method: 'GET',
        headers: h
      })
      .then(response=>response.json())
      .then(data => this.setState({countdata: data.count}))
    }

    render(){
    //   document.title="Xirka-13216108"
    // window.print()
      const {key,countdata} = this.state
      let token = key;
      let limit = countdata
      // while ((token === '') || (limit === '')){
      //   return ( 
      //     <div className="content-wrapper">
      //       <div className="loadingdata"> 
      //         <i className="fa fa-spinner fa-pulse" style={{fontSize:"150px", color:"#E43A34"}}> </i>
      //       </div>
      //     </div> )
      // }
        return (
          <div className="content-wrapper">
            <Switch>
              <Route exact path="/" component={Home} />
              <Route path="/login" render={()=>
              <div className="box-footer">
                You're Logged In
                <br></br>
                Go to <Link className="gotohome" to="/">Home</Link>
              </div>} />
              <Route path="/statistik" render={ () => <Statistik limit={limit}/> } />
              <Route path="/logpintu" render={ () => <Logpintu token={token} limit={limit}/> } />
              <Route path="/pengguna" render={ () => <Pengguna token={token} /> } />
              <Route path="/ruangan/" render={ () => <Ruangan token={token} /> } />
              <Route path="/laporan" render={ () => <Laporan limit={limit} token={token}/> } />
              <Route path="/daftarruangan" render={ () => <Daftarruangan token={token} /> } />  
              <Route path="/daftarpengguna" render={ () => <Daftarpengguna token={token} /> } />  
            </Switch>
          </div> 
        )
    }
}

export default withRouter(Content);
