import React,{Component} from 'react';
import {Link,withRouter} from 'react-router-dom';

class Daftarlogpintu extends Component{
  constructor(props) {
    super(props);
    this.state = {
    nim: '',
    terminalid:'',
    checkedtm:'',
    lockstatus:'',
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit(e){
    e.preventDefault();
    console.log(document.cookie[0])
  }

    render(){
        const {nim, terminalid, checkedtm, lockstatus} = this.state
        console.log(nim)
        console.log(terminalid)
        console.log(checkedtm)
        console.log(lockstatus)
        return (
            <div>
                <div className="paddingtop30px">
                </div>
                <div className="kotakfilter2"> 
                    <form className="kotakforminputlogpintu" onSubmit={this.handleSubmit}>
                    <div className="kotakinputpenggunascardid">
                        <label> NIM </label> <br></br>
                        <input name="nim" onChange={this.handleChange} className="inputformpenggunascardid" type="text" placeholder="NIM" ></input>
                    </div>

                    <div className="kotakinputpenggunanim">
                        <label>Terminal ID </label> <br></br>
                        <input name="terminalid" onChange={this.handleChange} className="inputformpenggunanim" type="text" placeholder="Terminal Id" ></input>
                    </div>

                    <div className="kotakinputpenggunanama">
                        <label> Checked Time </label> <br></br>
                        <input name="checkedtm" onChange={this.handleChange} className="inputformpenggunanama" type="datetime-local" ></input>
                    </div> 

                    <div className="kotakinputpenggunainstansi">
                        <label> Status </label> <br></br>
                        <select name="lockstatus" onChange={this.handleChange} className="inputformpenggunainstansi" type="" >
                            <option value="0"> Check In </option>
                            <option value="1"> Check Out </option>
                            <option value="2"> Izin </option>
                            <option value="3"> Sakit </option>
                        </select>
                    </div>

                    <div className="kotaksubmitpengguna">
                        <input className="submitformlogpintu" type="submit" value="Add"></input>
                    </div>

                    <div className="kotakcancelpengguna">
                        <Link to="/logpintu"> <span className="cancelformpengguna">Cancel</span></Link>
                    </div>
                    </form> 
                </div>
            </div>
        )
    } 
}
export default withRouter(Daftarlogpintu);
